    void setOrientationOf2DPlane(::Ifc4x3_rc3::IfcAxis2Placement3D* v);
    /// References to all load groups to be analyzed.
    boost::optional< aggregate_of< ::Ifc4x3_rc3::IfcStructuralLoadGroup >::ptr > LoadedBy() const;
    void setLoadedBy(boost::optional< aggregate_of< ::Ifc4x3_rc3::IfcStructuralLoadGroup >::ptr > v);
    /// References to all result groups available for this structural analysis model.
    boost::optional< aggregate_of< ::Ifc4x3_rc3::IfcStructuralResultGroup >::ptr > HasResults() const;
    void setHasResults(boost::optional< aggregate_of< ::Ifc4x3_rc3::IfcStructuralResultGroup >::ptr > v);
